		R(JZ(arbvbl2));	// 106 JE @VBL2
		R(LOOP(dffd));	// 108 loop dffd
	R(MOV(ax, 0x3));	// 110 mov ax,3h
	R(_INT(0x10));	// 111 int 10h
	R(MOV(edi, 1));	// 118 mov edi,1
	R(MOV(esi, 2));	// 119 mov esi,2
