		R(JMP(exitlabel));	// 121 JMP exitLabel
failure:
	R(MOV(al, 1));	// 123 mov al,1
exitlabel:
	R(MOV(ah, 0x4c));	// 125 mov ah,4ch
	R(_INT(0x21));	// 126 int 21h
 // Procedure affpal() start
affpal:
	R(PUSHAD);	// 130 pushad
	R(MOV(esi, offset(_data,pal_jeu)));	// 132 mov   esi,offset pal_jeu
	R(MOV(dx, 0x3c8));	// 133 mov   dx,3c8h
	al = 0;AFFECT_ZF(0); AFFECT_SF(al,0);	// 134 XOR   al,al
	R(OUT(dx, al));	// 135 out   dx,al
	R(MOV(dx, 0x3c9));	// 136 mov   dx,3c9h
	R(MOV(cx, 256*3));	// 137 mov cx,256*3
arbarbsaaccvaaaax:
LODSB;	// 139 LODSB
	R(OUT(dx, al));	// 140 out   dx,al
	R(DEC(cx));	// 141 dec cx
		R(JNZ(arbarbsaaccvaaaax));	// 142 JNZ  @@saaccvaaaax
	R(POPAD);	// 143 popad
	R(RETN);	// 144 ret


return;
